package TOB02;

public class For_Condicional {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double numeroFor = 0;
		
		//El resultado deberia retornar 4.0 devido a que esta se encuentra
		//inicializada en zero
		for(double i=0;i <= 3; i++) {numeroFor++;System.out.println(numeroFor);}
	}
}
